import React, { useState }  from "react";
import { RowFlex, ColumnFlex, Screen } from "../../styles";
import { Picture, HeavyText, LightText } from "./styles";
import { useTable } from "react-table";
import Button from "./Button";

export default function Main() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [ants, setAnts] = useState([]);
    const [startRaceState, setStartRace] = useState(0);
    const [getDataClicked, setGetDataClicked] = useState(false);

    const onGetDataClick = () => {
        setGetDataClicked(true);
        fetch("https://sg-ants-server.herokuapp.com/ants")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setAnts(Object.keys(result).map((key) => result[key])[0]);
              setGetDataClicked(false);
            },
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
        )
    }

    const onStartRaceClick = () => {
      ants.map(ant =>
        ant.calculation = generateAntWinLikelihoodCalculator()
      )
    };

    const generateAntWinLikelihoodCalculator = () => {
        const delay = 7000 + Math.random() * 7000;
        const likelihoodOfAntWinning = Math.random();

        return (callback) => {
          setTimeout(() => {
            callback(likelihoodOfAntWinning);
          }, delay);
        };
    }

    const tabledata = React.useMemo(
        () => ants
    )

    const tablecolumns = React.useMemo(
        () => [
          {
            Header: 'Name',
            accessor: 'name', // accessor is the "key" in the data
          },
          {
            Header: 'Length',
            accessor: 'length',
          },
          {
            Header: 'Color',
            accessor: 'color',
          },
          {
            Header: 'Weight',
            accessor: 'weight',
          },
        ],
        []
    )

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
      } = useTable({ tablecolumns, tabledata })

    const renderStep = () => {
        if (error) {
            return <div>Error retrieving ant data</div>;
          } else if (getDataClicked){
            return <div>Loading ant data...</div>;
          } else {
            return (
                <RowFlex>
                <ColumnFlex>
                <RowFlex>
                    <Button onClick={onGetDataClick}>GET DATA</Button>
                </RowFlex>
                <RowFlex>
                    <Button onClick={onStartRaceClick}>START RACE</Button>
                </RowFlex>
                <RowFlex>
                    STATE OF THE RACE
                </RowFlex>
                <RowFlex>
                <table {...getTableProps()} style={{ border: 'solid 1px black' }}>
                    <thead>
                        {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                            <th
                                {...column.getHeaderProps()}
                                style={{
                                background: 'aliceblue',
                                color: 'black',
                                fontWeight: 'bold',
                                }}
                            >
                                {column.render('Header')}
                            </th>
                            ))}
                        </tr>
                        ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                            {row.cells.map(cell => {
                                return (
                                <td
                                    {...cell.getCellProps()}
                                    style={{
                                    padding: '10px'
                                    }}
                                >
                                    {cell.render('Cell')}
                                </td>
                                )
                            })}
                            </tr>
                        )
                        })}
                    </tbody>
                    </table>
                </RowFlex>
                </ColumnFlex>
                </RowFlex>
            );
          }
    }

  return (
    <Screen>
        {renderStep()}
    </Screen>
  );
}
