import React, { useState }  from "react";
import { RowFlex, ColumnFlex, Screen } from "../../styles";
import { Picture, HeavyText, LightText } from "./styles";
import Button from "./Button";

export default function Main() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [ants, setAnts] = useState([]);
    const [startRaceState, setStartRace] = useState(0);
    const [getDataClicked, setgetDataClicked] = useState(false);
    const [antsState, setAntsState] = useState([]);

    const onGetDataClick = () => {
        setgetDataClicked(true);
        fetch("https://sg-ants-server.herokuapp.com/ants")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setAnts(result);
              setgetDataClicked(false);
              console.log(result.ants)
            },
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
        )
    }

    const onStartRaceClick = (event) => {
      setStartRace(event.target.value);
      console.log(startRaceState);
    };

    const validateFirstPage = () => {
    }

    const renderStep = () => {
        if (error) {
            return <div>Error retrieving ant data</div>;
          } else if (getDataClicked){
            return <div>Loading...</div>;
          } else {
            return (
                <RowFlex>
                <ColumnFlex>
                <RowFlex>
                    <Button onClick={onGetDataClick}>GET DATA</Button>
                </RowFlex>
                <RowFlex>
                    <Button>START RACE</Button>
                </RowFlex>
                <RowFlex>
                    <ul>    
                        {ants.ants}
                    </ul>
                </RowFlex>
                </ColumnFlex>
                </RowFlex>
            );
          }
    }

  return (
    <Screen>
        {renderStep()}
    </Screen>
  );
}
