import React, { useState, useEffect }  from "react";
import { useDispatch } from "react-redux";
import { RowFlex, ColumnFlex, Screen } from "../../styles";
import { Picture, HeavyText, LightText } from "./styles";
import { useTable } from "react-table";
import Button from "./Button";

export default function Main() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [ants, setAnts] = useState([]);
    const [refreshState, setRefreshState] = useState(false);
    const [getDataClicked, setgetDataClicked] = useState(false);
    const [raceState, setRaceState] = useState('Not yet run');
    const [likelihoods, setLikelihoods] = useState([]);
    const forceUpdate = useForceUpdate();

    useEffect(() => {
      console.log("HELLO")
    }, [ants]);

    function useForceUpdate(){
      const [value, setValue] = useState(0); // integer state
      return () => setValue(value => value + 1); // update the state to force render
    }

    const onGetDataClick = () => {
        setgetDataClicked(true);
        fetch("https://sg-ants-server.herokuapp.com/ants")
          .then(res => res.json())
          .then(
            (result) => {
              result.ants[0].calculation = 'Not yet run';
              result.ants[1].calculation = 'Not yet run';
              result.ants[2].calculation = 'Not yet run';
              result.ants[3].calculation = 'Not yet run';
              result.ants[4].calculation = 'Not yet run';

              result.ants[0].likelihood = 0;
              result.ants[1].likelihood = 0;
              result.ants[2].likelihood = 0;
              result.ants[3].likelihood = 0;
              result.ants[4].likelihood = 0;

              setIsLoaded(true);
              setAnts(Object.keys(result).map((key) => result[key])[0]);
              setgetDataClicked(false);

            },
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
        )
    }

    const onStartRaceClick = () => {
        setRaceState("In progress");
        const theAnts = ants.map(l => Object.assign({}, l));
        for (var i = 0; i < ants.length; i++){
          calculateLikelihood(theAnts[i], i);
          theAnts[i].calculation = 'In progress';
        }
        setAnts(theAnts);
        loop();
        console.log(ants);
      };

      const loop = () => {

        while (refreshState == true) {
          const theAnts = ants.map(l => Object.assign({}, l));
          setAnts(theAnts);
           if (refreshState == true){
            const theAnts = ants.map(l => Object.assign({}, l));
            setAnts(theAnts);
           }

           if (refreshState == true) setTimeout(loop, 0);
        }
      }



      const calculate = (ant) => {
        return new Promise((resolve) => {
          resolve(ant);
        });
      };

    const calculateLikelihood = (ant, index) => { 
      const generateCalculation = generateAntWinLikelihoodCalculator();

      return generateCalculation((ant) => {
        calculate(ant).then((calculation) => {
          ants[index].likelihood = calculation
        });
  });
    };

    const onconsoleClick2 = () => {
      console.log(ants);
    };

      function generateAntWinLikelihoodCalculator() {
        const delay = 7000 + Math.random() * 7000;
        const likelihoodOfAntWinning = Math.random();

        return (callback) => {
          setTimeout(() => {
            callback(likelihoodOfAntWinning);
          }, delay);
        };
      }

    var data = ants

    const columns = React.useMemo(
        () => [
          {
            Header: 'Name',
            accessor: 'name', // accessor is the "key" in the data
          },
          {
            Header: 'Length',
            accessor: 'length',
          },
          {
            Header: 'Color',
            accessor: 'color',
          },
          {
            Header: 'Weight',
            accessor: 'weight',
          },
          {
            Header: 'State of Calcluation',
            accessor: 'calculation',
          },
          {
            Header: 'Win Likelihood',
            accessor: 'likelihood',
          },
        ],
        []
    )

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
      } = useTable({ columns, data })

    const renderStep = () => {
        if (error) {
            return <div>Error retrieving ant data</div>;
          } else if (getDataClicked){
            return <div>Loading ant data...</div>;
          } else {
            return (
                <RowFlex>
                <ColumnFlex>
                <RowFlex>
                    <Button onClick={onGetDataClick}>GET DATA</Button>
                </RowFlex>
                <RowFlex>
                    <Button onClick={onStartRaceClick}>START RACE</Button>
                </RowFlex>
                <RowFlex>
                    <Button onClick={onconsoleClick2}>console</Button>
                </RowFlex>
                <RowFlex>
                    STATE OF THE RACE: {raceState}
                </RowFlex>
                <RowFlex>
                  {isLoaded == true && (
                    <p>{ants[0].likelihood}</p>
                  )}
                </RowFlex>
                <RowFlex>
                <table {...getTableProps()} style={{ border: 'solid 1px black' }}>
                    <thead>
                        {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                            <th
                                {...column.getHeaderProps()}
                                style={{
                                background: 'aliceblue',
                                color: 'black',
                                fontWeight: 'bold',
                                }}
                            >
                                {column.render('Header')}
                            </th>
                            ))}
                        </tr>
                        ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                            {row.cells.map(cell => {
                                return (
                                <td
                                    {...cell.getCellProps()}
                                    style={{
                                    padding: '10px'
                                    }}
                                >
                                    {cell.render('Cell')}
                                </td>
                                )
                            })}
                            </tr>
                        )
                        })}
                    </tbody>
                    </table>
                </RowFlex>
                </ColumnFlex>
                </RowFlex>
            );
          }
    }

  return (
    <Screen>
        {renderStep()}
    </Screen>
  );
}
